mapper.select:
SELECT COUNT(0) FROM Manager m INNER JOIN
ManagerVenue mv ON m.`ID` = mv.`ManagerID`
INNER JOIN Venue v ON mv.`VenueID` = v.`ID` WHERE m.`ID` = #{managerid} AND v.`ID` = #{venueid}

SELECT m.* FROM Manager m INNER JOIN
ManagerVenue mv
ON m.`ID` = mv.`ManagerID` WHERE mv.`VenueID` = #{venueid}

mapper.insert:
insert into Manager (ID, CreateTime, ModifyTime,
Name, Phone, Password,
Type, RealName, AppAuthToken
)
values (#{id,jdbcType=CHAR}, #{createtime,jdbcType=TIMESTAMP}, #{modifytime,jdbcType=TIMESTAMP},
#{name,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR},
#{type,jdbcType=INTEGER}, #{realname,jdbcType=INTEGER}, #{appauthtoken,jdbcType=VARCHAR}
)
