mapper.select:
SELECT tj.*,  , m.`Name` mName
FROM TrainJoin tj INNER JOIN Train t ON tj.`TrainID` = t.`ID` LEFT JOIN Member m ON m.`ID` = tj.`MemberID`
WHERE tj.`AmountID` = #{amountid}

SELECT tj.*,  , m.`Name` mName
FROM TrainJoin tj INNER JOIN Train t ON tj.`TrainID` = t.`ID` LEFT JOIN Member m ON m.`ID` = tj.`MemberID`
WHERE tj.`TrainID` = #{id}

mapper.insert:
insert into TrainJoin (ID, CreateTime, ModifyTime, 
  TrainID, MemberID, Price, 
  Content, Type, PayType, 
  PayRemark, AmountID, AmountType
  )
values (#{id,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}, #{modifytime,jdbcType=TIMESTAMP}, 
  #{trainid,jdbcType=VARCHAR}, #{memberid,jdbcType=VARCHAR}, #{price,jdbcType=DOUBLE}, 
  #{content,jdbcType=VARCHAR}, #{type,jdbcType=INTEGER}, #{paytype,jdbcType=INTEGER}, 
  #{payremark,jdbcType=VARCHAR}, #{amountid,jdbcType=VARCHAR}, #{amounttype,jdbcType=INTEGER}
  )
