mapper.select:
select

from MassPersonBuyWant mpbw
where ID = #{id,jdbcType=CHAR}

select

,


	from MassPersonBuyWant mpbw LEFT JOIN (SELECT * FROM
	EmpCustPerson WHERE EmpId = '${empId}') ecp ON ecp.`HouseId` =
	mpbw.`ID`


	from MassPersonBuyWant mpbw INNER JOIN (SELECT * FROM
	EmpCustPerson WHERE EmpId = '${empId}') ecp ON ecp.`HouseId` =
	mpbw.`ID`



		AND mpbw.District=#{districtNa}


		AND mpbw.District in

			#{item}



		AND mpbw.Type=#{houseType}


		AND mpbw.CountF=#{roomType}


		AND mpbw.Square >= #{minHouseArea}


		AND mpbw.Square <= #{maxHouseArea}


		AND mpbw.Collectdate >= #{startTime}


		AND mpbw.Collectdate <= #{endTime}


		AND mpbw.Price >= #{minPrice}


		AND mpbw.Price <= #{maxPrice}


		AND mpbw.Site=#{webName}


		AND mpbw.Site in

			#{item}



		AND mpbw.regtel not in

			#{item}
